function draw(ctx, control) {
  ctx.fillStyle = '#333333';
  ctx.fillRect(0, 0, control.width, control.height);
  ctx.strokeStyle = '#555';
  ctx.strokeRect(0, 0, control.width, control.height);
  ctx.fillStyle = '#ffffff';
  ctx.textAlign = 'center';
  ctx.textBaseline = 'middle';
  ctx.font = '14px ' + (control.fontname || 'sans-serif');
  ctx.fillText(control.caption, control.width/2, control.height/2);
}

function draw(ctx, control) {
  ctx.fillStyle = control.textcolor;
  ctx.font = '14px ' + (control.fontname || 'sans-serif');
  ctx.textAlign = 'left';
  ctx.textBaseline = 'top';
  if (control.multiline && control.text.indexOf('\n') !== -1) {
    control.text.split('\n').forEach(function(line, i) {
      ctx.fillText(line, 0, i * 16);
    });
  } else {
    ctx.fillText(control.text, 0, 0);
  }
}

