function draw(ctx, control) {
  ctx.fillStyle = '#555';
  ctx.fillRect(0, 0, control.width, control.height);
  if (control.hasborder) {
    ctx.strokeStyle = '#777';
    ctx.strokeRect(0, 0, control.width, control.height);
  }
  var thumbRadius = control.height * 0.4;
  var thumbX = control.checked ? (control.width - thumbRadius*2) : 0;
  var centerY = control.height/2;
  ctx.fillStyle = '#222';
  ctx.beginPath();
  ctx.arc(thumbX + thumbRadius, centerY, thumbRadius, 0, 2*Math.PI);
  ctx.fill();
  ctx.strokeStyle = '#777';
  ctx.stroke();
  ctx.fillStyle = control.textcolor;
  ctx.font = '12px ' + (control.fontname || 'sans-serif');
  ctx.textAlign = 'center';
  ctx.textBaseline = 'middle';
  ctx.fillText(control.text, control.width/2, centerY);
}

function draw(ctx, control) {
  ctx.fillStyle = '#555';
  ctx.fillRect(0, control.height/2 - 4, control.width, 8);
  var sliderPos = (parseFloat(control.value)||0)/100 * control.width;
  ctx.fillStyle = '#0a84ff';
  ctx.fillRect(sliderPos - 5, 0, 10, control.height);
}

function draw(ctx, control) {
  ctx.fillStyle = '#444';
  ctx.fillRect(0, 0, control.width, control.height);
  ctx.strokeStyle = '#555';
  ctx.strokeRect(0, 0, control.width, control.height);
  ctx.strokeStyle = '#777';
  ctx.beginPath();
  ctx.moveTo(control.width/2, 0);
  ctx.lineTo(control.width/2, control.height);
  ctx.stroke();
  ctx.fillStyle = '#fff';
  ctx.font = '14px ' + (control.fontname||'sans-serif');
  ctx.textAlign = 'center';
  ctx.textBaseline = 'middle';
  ctx.fillText('One', control.width/4, control.height/2);
  ctx.fillText('Two', 3*control.width/4, control.height/2);
}

function draw(ctx, control) {
  var img = new Image();
  img.onload = function() { ctx.drawImage(img, 0, 0, control.width, control.height); };
  img.src = control.url;
}

function draw(ctx, control) {
  var opts = control.options || [];
  opts.forEach(function(option,i) {
    var y = 10 + i*20;
    ctx.strokeStyle = control.textcolor;
    ctx.beginPath();
    ctx.arc(10, y, 8, 0, 2*Math.PI);
    ctx.stroke();
    if (control.selected === option) {
      ctx.fillStyle = '#0a84ff';
      ctx.beginPath();
      ctx.arc(10, y, 5, 0, 2*Math.PI);
      ctx.fill();
    }
    ctx.fillStyle = control.textcolor;
    ctx.font = '14px ' + (control.fontname||'sans-serif');
    ctx.textAlign = 'left';
    ctx.textBaseline = 'middle';
    ctx.fillText(option, 25, y);
  });
}

function draw(ctx, control) {
  var opts = control.options || [];
  opts.forEach(function(option,i) {
    var y = 10 + i*20;
    ctx.strokeStyle = control.textcolor;
    ctx.strokeRect(0, y-10, 20, 20);
    var isChecked = Array.isArray(control.checked)
      ? control.checked.indexOf(option) !== -1
      : control.checked;
    if (isChecked) {
      ctx.beginPath();
      ctx.moveTo(0, y-10);
      ctx.lineTo(20, y+10);
      ctx.moveTo(20, y-10);
      ctx.lineTo(0, y+10);
      ctx.stroke();
    }
    ctx.fillStyle = control.textcolor;
    ctx.font = '14px ' + (control.fontname||'sans-serif');
    ctx.textAlign = 'left';
    ctx.textBaseline = 'middle';
    ctx.fillText(option, 25, y);
  });
}

