function draw(ctx, control) {
  ctx.fillStyle = '#555';
  ctx.fillRect(0, 0, control.width, control.height);
  var prog = (parseFloat(control.progress)||0)/100 * control.width;
  ctx.fillStyle = control.color;
  ctx.fillRect(0, 0, prog, control.height);
  ctx.strokeStyle = '#555';
  ctx.strokeRect(0, 0, control.width, control.height);
}

function draw(ctx, control) {
  var prog = (parseFloat(control.progress)||0)/100;
  var cx = control.width/2, cy = control.height/2;
  var r = Math.min(control.width, control.height)/2 - 5;
  ctx.lineWidth = 10;
  ctx.strokeStyle = '#444';
  ctx.beginPath();
  ctx.arc(cx, cy, r, 0, 2*Math.PI);
  ctx.stroke();
  ctx.strokeStyle = '#0a84ff';
  ctx.beginPath();
  ctx.arc(cx, cy, r, -Math.PI/2, -Math.PI/2 + 2*Math.PI*prog);
  ctx.stroke();
}

function draw(ctx, control) {
  ctx.fillStyle = control.backgroundcolor;
  ctx.fillRect(0, 0, control.width, control.height);
  ctx.strokeStyle = '#555';
  ctx.strokeRect(0, 0, control.width, control.height);
  ctx.fillStyle = control.textcolor;
  ctx.font = '14px ' + (control.fontname||'sans-serif');
  ctx.textAlign = 'left';
  ctx.textBaseline = 'middle';
  var txt = (control.options&&control.options[0])||'';
  ctx.fillText(txt, 4, control.height/2);
  ctx.beginPath();
  ctx.moveTo(control.width-20, control.height/2-5);
  ctx.lineTo(control.width-10, control.height/2-5);
  ctx.lineTo(control.width-15, control.height/2+5);
  ctx.closePath();
  ctx.fill();
}

